mapper.insert:

  SELECT LAST_INSERT_ID()

insert into comment ( cid, uid,
  state, createtime, content
  )
values (#{cid,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER},
  #{state,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
select id, cid, uid, state, createtime, content
from comment
where id = #{id,jdbcType=INTEGER}
