mapper.update:
update expertsinfo
email= #{email,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, photoid = #{photoid,jdbcType=INTEGER}, phone = #{phone,jdbcType=VARCHAR}, account = #{account,jdbcType=VARCHAR}, realname = #{realname,jdbcType=VARCHAR}, academic = #{academic,jdbcType=VARCHAR}, state = #{state,jdbcType=INTEGER}, createtime = #{createtime,jdbcType=TIMESTAMP}, isvisible = #{isvisible,jdbcType=INTEGER}, employer = #{employer,jdbcType=LONGVARCHAR}, summary = #{summary,jdbcType=LONGVARCHAR}, openemail = #{openemail,jdbcType=VARCHAR}, otheracademic = #{otheracademic,jdbcType=VARCHAR}, enname = #{enname,jdbcType=VARCHAR}, motto = #{motto,jdbcType=VARCHAR}, id=#{id,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select a.id, email, password, photoid, phone, account, realname, academic, state, createtime, isvisible, enname, openemail, employer, summary,url,otheracademic,motto from expertsinfo a LEFT JOIN sys_file b ON a.photoid=b.id where a.state=1
and a.id=#{id}
and a.realname is NOT NULL
and a.email=#{email}
ORDER BY a.createtime DESC

select  id, email, password, photoid, phone, account, realname, academic, state, createtime,
isvisible, enname, openemail, employer, summary,otheracademic,motto
from expertsinfo


     AND state=1



    AND  id = #{id,jdbcType=INTEGER}


    AND  account = #{account,jdbcType=VARCHAR}


    AND  email = #{email,jdbcType=VARCHAR}


    AND  email = #{email,jdbcType=VARCHAR}


    AND  password = #{password,jdbcType=VARCHAR}


    AND  phone = #{phone,jdbcType=VARCHAR}
