mapper.insert:

  SELECT LAST_INSERT_ID()

insert into news (id, title, state, 
  photoid, createtime, content
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{state,jdbcType=INTEGER}, 
  #{photoid,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
select id, title, state, photoid, createtime, content from news where state=0
and id=#{id}
ORDER BY createtime DESC
